INSERT INTO cscart_language_values (`lang_code`, `name`, `value`, `original_value`) VALUES ('en', 'text_pay4later_notice', '<b>Note</b>: In order to track your Pay4Later orders with the shopping cart software, you have to take these steps:<br /><br />-&nbsp;Log in to Pay4Later BackOffice<br />-&nbsp;Click on the <u>\'Settings/Installations\'</u> link in the <u>\'Quick Links\'</u> section.<br />-&nbsp;Set <u>\'Return URL (Verified)\'</u> setting to:<br /><b>[verified_url]</b><br />-&nbsp;Set <u>\'Return URL (Decline)\'</u> setting to:<br /><b>[decline_url]</b><br />-&nbsp;Set <u>\'Return URL (Refer)\'</u> setting to:<br /><b>[refer_url]</b><br />-&nbsp;Set <u>\'Return URL (Cancel)\'</u> setting to:<br /><b>[cancel_url]</b><br />-&nbsp;Set <u>\'CSN URL\'</u> setting to:<br /><b>[process_url]</b><br />-&nbsp;Click on the <u>\'Save Changes\'</u> button<br />', '<b>Note</b>: In order to track your Pay4Later orders with the shopping cart software, you have to take these steps:<br /><br />-&nbsp;Log in to Pay4Later BackOffice<br />-&nbsp;Click on the <u>\'Settings/Installations\'</u> link in the <u>\'Quick Links\'</u> section.<br />-&nbsp;Set <u>\'Return URL (Verified)\'</u> setting to:<br /><b>[verified_url]</b><br />-&nbsp;Set <u>\'Return URL (Decline)\'</u> setting to:<br /><b>[decline_url]</b><br />-&nbsp;Set <u>\'Return URL (Refer)\'</u> setting to:<br /><b>[refer_url]</b><br />-&nbsp;Set <u>\'Return URL (Cancel)\'</u> setting to:<br /><b>[cancel_url]</b><br />-&nbsp;Set <u>\'CSN URL\'</u> setting to:<br /><b>[process_url]</b><br />-&nbsp;Click on the <u>\'Save Changes\'</u> button<br />');
INSERT INTO cscart_language_values (`lang_code`, `name`, `value`, `original_value`) VALUES ('en', 'text_hsbc_notice', '<b>Client ID  -</b> Your Client ID - a decimal value between 0 and 999999999, or a valid Client alias of the form UK12345678CUR. <br /> Note, the alias is case sensitive. <br /> <b>Important note for UNIX users:</b><br /> Please make sure you have appropriate permissions for the following directory: [cart_dir] (shell command \"chmod -R 755\").', '<b>Client ID  -</b> Your Client ID - a decimal value between 0 and 999999999, or a valid Client alias of the form UK12345678CUR. <br /> Note, the alias is case sensitive. <br /> <b>Important note for UNIX users:</b><br /> Please make sure you have appropriate permissions for the following directory: [cart_dir] (shell command \"chmod -R 755\").');
