INSERT INTO cscart_language_values (`lang_code`, `name`, `value`, `original_value`) VALUES ('en', 'payments.cresecure.location_notice', '<b>Please note:</b><br>1. This payment requires the https checkout option to be enabled!<br>2. By default, the Checkout location is shown to a user on payment placing. In order to show a different location, create a new location with the dispatch \'checkout.cresecure_template\' in the <a href=\"[url]\" target=\"_blank\">Layout manager</a>.', '<b>Please note:</b><br>1. This payment requires the https checkout option to be enabled!<br>2. By default, the Checkout location is shown to a user on payment placing. In order to show a different location, create a new location with the dispatch \'checkout.cresecure_template\' in the <a href=\"[url]\" target=\"_blank\">Layout manager</a>.');
